//TelemetryOnTests - Overview
let BeginningOfPeriod = 
traces
| where timestamp between (_startTime .. _startTime + 2d)
| where tostring(customDimensions.eventId) == "ALIFCTST0003"
| extend CodeunitName = tostring(customDimensions.alName)
    , MethodName = tostring(customDimensions.alMethodName)
    , NoOfSqlStatements = toint(customDimensions.alNoOfSQLStatements)
| extend Method = strcat(CodeunitName,'.',MethodName)
| where Method contains _MessageFilter or message  contains _MessageFilter
| summarize avgNoOfSqlStatements=avg(NoOfSqlStatements) by Method;
let EndOfPeriod = 
traces
| where timestamp between (_endTime - 2d .. _endTime)
| where tostring(customDimensions.eventId) == "ALIFCTST0003"
| extend CodeunitName = tostring(customDimensions.alName)
    , MethodName = tostring(customDimensions.alMethodName)
    , NoOfSqlStatements = toint(customDimensions.alNoOfSQLStatements)
| extend Method = strcat(CodeunitName,'.',MethodName)
| where Method contains _MessageFilter or message  contains _MessageFilter
| summarize avgNoOfSqlStatements=avg(NoOfSqlStatements) by Method;
let Difference = 
BeginningOfPeriod
| join kind=leftouter EndOfPeriod on $left.Method == $right.Method
| extend Difference =  avgNoOfSqlStatements1 - avgNoOfSqlStatements
| order by Difference desc
| take 100;
traces
| where timestamp between (_startTime .. _endTime)
| where tostring(customDimensions.eventId) == "ALIFCTST0003"
| extend
    CodeunitId = tostring(customDimensions.alCodeunitId)
    , CodeunitName = tostring(customDimensions.alName)
    , MethodName = tostring(customDimensions.alMethodName)
    , NoOfSqlStatements = toint(customDimensions.alNoOfSQLStatements)
| extend Method = strcat(CodeunitName,'.',MethodName)
| join kind=rightouter Difference on $left.Method == $right.Method
| project timestamp, Method, NoOfSqlStatements
| summarize avgNoOfSqlStatements=avg(NoOfSqlStatements) by Method, bin(timestamp, 1d)
| order by Method
